use replace-with-usercode_whiskey_retail_shop;

LOAD DATA LOCAL INFILE '/home/controlm/payments.csv'
INTO TABLE payments
FIELDS TERMINATED BY ',' 
(@col1,@col2,@col3,@col4,@col5,@col6,@col7) set payment_id=@col2, date=@col3, customer_id=@col4, employee_id=@col5, product_id=@col6, price=@col7;

DELETE FROM payments
WHERE payment_id = 0;
